import React, { useEffect, useState } from "react";
import DelayList from "./DelayList";
import VanList from "./VanList";
import { fetchDashboardData } from "../api";

const Dashboard = () => {
  const [delays, setDelays] = useState([]);
  const [vanRequirements, setVanRequirements] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const data = await fetchDashboardData();
        setDelays(data.delays);
        setVanRequirements(data.van_requirements);
        setLoading(false);
      } catch (err) {
        setError("Failed to load data. Please try again later.");
        setLoading(false);
      }
    };

    loadData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div>
      <h2>Bus Delays</h2>
      <DelayList delays={delays} />
      <h2>Van Requirements</h2>
      <VanList vans={vanRequirements} />
    </div>
  );
};

export default Dashboard;
